package base;

public class swapNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		int b=20;
		System.out.println("Before swapping: a="+a+" b="+b);
		a=a+b; //30
		b=a-b; //10
		a=a-b; //20
		System.out.println("After swapping: a="+a+" b="+b);
		

	}

}
